# Function to load ROC data from file and plot it
def plot_roc_from_file(fpr, tpr, auc, std, label, color, linestyle):
    fpr = fpr
    tpr = tpr
    auc = auc  # AUC is the same for all rows, so take the first
    std = std
    plt.plot(fpr, tpr, label=f'{label} ({auc:.2f} $\pm$ {std: .2f})', color=color, linestyle=linestyle, lw=2, alpha=.8)

